css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("livejournal.com") {
#lj_controlstrip { background-color: #292929 !important; }
#lj_controlstrip_user > A:last-child, 
#lj_controlstrip_search > A, 
#lj_controlstrip_actionlinks > SPAN:first-child + BR + A + A + A
     { display: none !important; }
TD { padding: 0 5px !important; }
#beta-inner * TD { padding: 0 !important; }
#lj_controlstrip_userpic { padding-left: 0 !important; }
}

@-moz-document url("http://www.livejournal.com/update.bml") {

#navigation, 
#Logo, 
H1, 
#ljbreadcrumbs, 
#htmltools, 
#infobox, 
#MiniSiteMap, 
#footer { 
     display: none!important; }
#content { 
     position: absolute !important; 
     top: 0 !important; 
     left: 0 !important; }
#draft { height: 275px; }
}
