css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("utterz.com") {
 
    /* applies font to all generic HTML tags */
    a,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    p,
    label,
    html,
    body,
    span,
    div,
    table,
    tr,
    td,
    br,
    input,
    font,
    button,
    select,
    option,
    textarea {
        font-family: Tahoma !important;
        font-size: 8pt !important;
        text-decoration: none !important;
    }

    .utts-list h3 {
        text-align: justify;
        direction: rtl;
    }

    .utts-list h3 img {
        padding-left: 4px;
    }

    .utt-text {
        text-align: justify;
        direction: rtl;
    }

    .utt-reply {
        text-align: justify;
        direction: rtl;
    }

    .usr-details {
        background: #fff;
    }

    input, textarea, select, option {
        direction: rtl;
        text-align: justify;
    }

}
