css:
@-moz-document domain("himasoku.com") {
div.blog-title-outer, #sub, div.column-inner-2, #extra,
a[href*=accesstrade], div.popularArticlesWithImage, div.pager,
#NnVD, div.article-body-more>dl>table, iframe, div.dashed
{ display: none !important; }

body {
  background: none !important;
  background-color: #f4f4f4 !important;
}
div.article-outer { background-color: #fafafa !important; }
div#container { width: 812px !important;}

div.article-outer { border: 1px solid #ddd !important; }
div.article-header { border-bottom: 1px solid #ddd !important; }
div.article-body { border-top: 1px solid #ddd !important; }
}
