css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {

#logo {height: 0 !important;
        width: 0 !important;
	padding-left: 240px !important;
	padding-top: 70px !important;
	background: url('http://24.media.tumblr.com/15ab29b5df63880f80e5da615c2f97b5/tumblr_mlx0umCctM1s2cle4o1_250.png') no-repeat !important;}}

----------------------------------------------------LOGO ENDS--------------------------------------------------------

@-moz-document url-prefix('http://www.tumblr.com') {

#popover_blogs .tab_notice:after{display:none}div.l-header .tab.iconic > a {background-image:url('http://25.media.tumblr.com/fedeba1986e56bcce68660466ef8adcd/tumblr_mlx46i3LTq1s2cle4o1_r4_250.png') !important;}

}

-------------------------------------------------DASHBORD BUTTONS END------------------------------------------------

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/')
{

.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Ninja Scrolls" !important;
}
}


