css:
@-moz-document domain("zotero.org") {
#logohead img {
    height: 20px;
}

#purchase-storage-link {
    display: none;
}

h1 {
  line-height: 0;
}

#content .center {
    padding: 8px 20px 0;
    width: auto;
	-webkit-box-shadow: none;
	box-shadow: none;
    -webkit-border-radius: none;
	border-radius: none;
}

footer .center {
  	width: auto;
}

header .center {
  	padding-top: 10px;
    width: 95%;
}

.minor-col {
    width: 19%;
    min-width: 100px;
}
  
.major-col {
    float:left;
    width: 80%;
    min-width: 400px;
}

th, td {
  padding: 1px;
}

#breadcrumbs {
	position: absolute;
	top: 10px;
	left: 130px;
}
}
