css:
@-moz-document url-prefix(http://www.tumblr.com/),url-prefix(https://www.tumblr.com/){

/* i really want this to be global */

#popover_blogs .tab_notice:after{display:none; }

.l-header .iconic>a{
		background-image:url('https://31.media.tumblr.com/8fe4b0a5284d195ed5b1fa2b4551f09f/tumblr_inline_mxx2deblci1qkv2pp.png') !important;}
    
     #xkit-control.tab.iconic > a,

#xkit-control.tab.iconic.selected > a {

  background-image: url("http://media.tumblr.com/1555685bb2c62b853a677b15aedf6862/tumblr_inline_njqlxmrSr51qkv2pp.png") !important;
      background-size: 28px 28px !important;}
  
  #account_button {
    padding-right: -20px !important;
}
#account_button a {
    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 34px !important;
    padding-top: 34px !important;
    background: url('http://media.tumblr.com/c4af3d3e657eb757f89e79d83460abb1/tumblr_inline_ngc74y36G51qkv2pp.png') !important;}
  .tab_bar .iconic a.tab_anchor {
  background-size: 175px 36px;
}
    #inbox_button a, #account_button a, #discover_button a, #home_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 34px !important;
    padding-top: 36px !important;
}

.tab_bar .tab_inbox .tab_anchor {background-position: -70px 0}
  .tab_bar .tab_discover .tab_anchor {background-position: -35px 0}
.tab_bar .tab_home .tab_anchor {background-position: 0 0}
.tab_bar .tab.iconic:hover .tab_anchor,.tab_bar .tab.iconic:active .tab_anchor {
  opacity: .7;
}
.tab_bar .tab.iconic:hover .tab_anchor,.tab_bar .tab.iconic:active .tab_anchor {opacity: .7;}
.tab.iconic > .tab_anchor::before {content: none !important;}
.tab.iconic > .tab_anchor i::before {content: none !important;}

}
