css:
@-moz-document domain("www.jutarnji.hr") {
body
{
  font-family: 'Helvetica Neue', 'Helvetica', sans-serif;
}

#widget, #navigation-v2, #topteme, .catchus-v2 { display: none; }

/* hide top ticker */
#top-ticker { display: none; }

/* hide jutarnji online */
.jutarnjionline { display: none; }

/* hide facebook recommendations */
.mar10pxbot, .mar15pxbot { display: none; }

#forecast { display: none; }

#horoskop-box { display: none; }

img
{
  box-shadow: 5px 5px 5px #ccc;
}

/* styling news headers */

div.block-section-b10 p.head,
div.block-section-b11-1 p.head,
div.block-section-b11-2 p.head,
div.block-section-b6 p.head
{
  font-style: italic;
  margin-bottom: 5px;
}

.block-section-b10 h1,
.block-section-b11-1 h1,
.block-section-b6 h1
{
  font-size: 1.2em;
  margin-bottom: 10px;
}
.block-section-b11-2 h1
{
  font-size: 1.1em;
  margin-bottom: 10px;
}

.image-unit div a.image-unit-h1
{
  font-size: 1.8em;
  line-height: normal;
}

.image-unit div a.image-unit-h3
{
  font-size: 1em;
}

/* sizes for news boxes */

#topnews
{
  margin-bottom: 20px;
}

div.block-section-b10.span-4
{
  width: 300px;
  margin-right: 32px;
}

div.block-section-b10.span-4.last
{
  width: 300px;
  margin-right: 12px;
}

div.container div.span-8.last
{
  margin-top: 20px;
  margin-bottom: 20px;
}

/* changes on individual subpages */

}
