css:
@-moz-document domain("techcrunch.com") {

header.header, .announcement, .l-sidebar, .social-share, footer.footer,
.article-extra, .next-link, .social-flw, #bit
{ display: none !important; }

h1 {
  font-size: 20px !important;
}

div[role="main"] {
  width: 800px;
}
.l-two-col>.l-main-container>.l-main {
  margin-right: 0;
  padding-right: 0;
}
.l-two-col-expose>.l-main-container>.l-main {
  margin-left: 0;
  padding-left: 0;
}

}
