css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.hulu.com") {

table#sortable-queue,
table#sortable-queue th,
table#sortable-queue td,
table#sortable-queue td a {
   font-size: 24px !important;
}

table#sortable-queue td.c3,
table#sortable-queue td.rh-c3,
table#sortable-queue td.c5,
table#sortable-queue td.rh-c5 {
   font-size: 16px !important;
}

table#sortable-queue td.c0,
table#sortable-queue td.rh-c0,
table#sortable-queue td.c1,
table#sortable-queue td.rh-c1,
table#sortable-queue td.c7,
table#sortable-queue td.rh-c7,
table#sortable-queue td.c6,
table#sortable-queue td.rh-c6 {
   display: none !important;
}

table#sortable-queue td.c2 a img {
   width: 145px !important;
   height: 80px !important;
}

div#info_hover div.content b,
div#info_hover div.content p {
   font-size: 18px !important;
}

div#page-content > div.fluid > div:first-child,
div#email-alerts,
div#queue-actions {
   display: none !important;    
}

body div.fixed-lg {
    width: 95% !important;
}

div.listable div.expander {
    width: 80% !important;
}

div.show-container div.header span.header-content,
div.show-container div.header span.header-content span.video-count {
    color: white !important;
    background-color: white !important;
}

div.show-container div.header span.header-content a.show-name {
    color: #4c4c4c !important;
}

div.show-container input,
div.show-container div.header span.header-content a.play-all,
div#queue div.show-container div.expander-content div.expander table tr.rh td,
div#queue div.show-container div.expander-content div.expander table tr.r td.c2,
div#queue div.show-container div.expander-content div.expander table tr.r td.c3 {
    display: none !important;
}

div.show-container a.show-name {
   font-size: 28px !important;
   background: none !important;
}

div#queue div.show-container div.expander-content div.expander table tr.r td.c0,
div#queue div.show-container div.expander-content div.expander table tr.r td.c1,
div#queue div.show-container div.expander-content div.expander table tr.r td.c4 {
    font-size: 22px !important;
}

div#queue div.show-container div.expander-content div.expander table tr.r td.c0 {
    width: 70px !important;
}

div.play-button-hover a img.thumbnail {
   width: 145px !important;
   height: 80px !important;
}

div#info_hover div.content b,
div#info_hover div.content p {
   font-size: 24px !important;
}
}
