css:
@-moz-document domain("wikipedia.org") {
#bodyContent #siteSub, .globegris
{
  display: none;
}

#bodyContent
{
  zoom: 130%;
}

#mw-fr-reviewnotice, .editsection, .infobox, .metadata, .navbox, .catlinks, .toc, .references-small
{
  zoom: 80%;
}

#mw-content-text p
{
  text-align: justify !important;
}

div[style*="radius"]
{
  border-radius: 0 !important;
}
}
