css:
@-moz-document url-prefix("http://4pda.ru/forum/") {
div#go_1.globalmesswrap, #logostrip, div.borderwrap[style="margin-bottom:6px;"], .copyright, div[style="text-align:center"], #ipb_link_ad, div#fo_stat.borderwrap, .toplinks,
div[style="overflow-y:hidden;"], .catend, .borderwrap-header tbody
{
  display:none !important;
}

.postcolor
{
  font-size:140% !important;

}

.hidetop
{
  font-size:100% !important;

}

#navstrip
{
  font-size:160% !important;

}

.ipbtable .row2, .ipbtable .row1
{
  zoom: 130%;
}

.forumdesc b
{
  font-weight: normal !important;
  font-style: italic;
  zoom: 80%;
}

.forumdesc i
{
  display: none;
}

#fo_-1 .ipbtable .row2, #fo_-1 .ipbtable .row1
{
  zoom: 100% !important;
}

#fo_-1 .ipbtable div
{
  margin: 5px 0 0 0;
}

hr.sfr
{
  border-top: solid 1px rgb(185, 206, 243);
}

.forumdesc
{
  margin-top: 20px !important;
}

.maintitle
{
  background-image : none;
  background-color : #2982CC;
}

table th, .borderwrap table th, .subtitle, .subtitlediv, .postlinksbar
{
  background-image : none;
}

.ipbtable td[width="1%"]
{
  zoom: 85% !important;
}
}
