css:
@-moz-document url-prefix("https://togetter.com/li/") {
.header_info_box ,#fixed_social_footer,
.header_navigator, #header, [class*="advertise"], .right,
.side_category, .ad_custom, form, .side_box, #footer, .title_box,
.amazon_reco_box, .simple_list, .ad_full_custom, #gotop,
#social_floating_box, #gototop, [id*="kouhaku"], ins, .ad_list_top
{ display: none !important; }

body {
  color: #333 !important;
  background-image: none;
  background-color: #E8F4F2 !important;
}
body:before {
  background-image: none !important;
}
.contents {
  margin: 2em auto !important;
}
.contents, .contents .contents_main, .contents .left {
  float: none !important;
  width: 800px !important;
  padding: 20px !important;
  background-color: #fff !important;
}
.contents .contents_main {
  background-color: inherit !important;
  border: 0 !important;
  width: auto !important;
}
.contents .left {
  width: auto !important;
}
.tweet_wrap {
  border: 1px solid #f5f5f5 !important;
  background-color: #fafafa !important;
}
.comment_box .comment {
  background-color: #f6fafa !important;
  border: 1px solid #eee !important;
  padding: 5px !important;;
}

/* Tweet box */
.tweet_box .icon_48,
.tweet_box .icon_73 {
  width: 30px;
  height: 30px;
  border-radius: 5px;
}
.tweet_box .list_tweet_box {
  padding-left: 43px;
}
.tweet_box .status {
  display: none;
}
.tweet_box .list_tweet_box .tweet_wrap {
  padding: 8px;
}
.tweet_box .list_box {
  margin-bottom: 5px;
}
.tweet_box .user_link strong,
.tweet_box .user_link .status_name {
  color: #ddd;
}
.tweet_box .timestamp {
  color: #ddd;
}
.tweet_box .tweet {
  line-height: 1.5em;
}
.tweet_box .tweet a[data-origin-url^="http://twitter.com"],
.tweet_box .tweet a[data-origin-url^="https://twitter.com"] {
  color: #75AEBD;
  font-size: smaller;
  font-weight: 300;
}
.tweet_box .tweet_quote {
  display: none;
}

/* #Hashtag */
a[href*="twitter.com/search"] {
  color: #A1BCDD !important;
}
.balloon_body {
  border-color: #eee !important;
  box-shadow: 0 1px 0 #e8e8e8 !important;
}
#hidden_tweet_box {
  margin: 40px 0 80px;
}

}

@-moz-document url-prefix("http://togetter.com") {
ins, .editor_recommend, #fixed_header,
.ad_rectangle
{ display: none !important; }
}
