css:
@-moz-document url-prefix("http://blog.livedoor.jp/nwknews/") {

  div.blog-title-outer, #nav, #sub, #extra, div.ad-center, *[class*=google],
  div.column-inner-2>table, div.pager, div[align=right]+span,
  div.article-body-more>table, div.article-footer, #ld-topics,
  div.related-articles, #tweetlist, #comment-form, body>center, div.footer-outer
  { display: none !important; }

  div.article-outer {
    margin: 2em auto !important;
  }

}
