css:
@-moz-document url("chrome://speeddial/content/speeddial.xul"){
	
	.speeddialThumb {
		background: #ffffff;
		margin:25px 9px 0px 9px !important;
		padding:9px;
		cursor:pointer;
		-webkit-box-shadow:  0px 2px 2px 0px rgba(0, 0, 0, 0.15);
		box-shadow:  0px 2px 2px 0px rgba(0, 0, 0, 0.15);
		border-radius: 2px;
		-moz-transition: margin 0.2s ease-in-out;
	}
	
	.speeddialThumb:hover {
	    background:#f7f9f9;
		-webkit-box-shadow:  0px 2px 2px 0px rgba(0, 0, 0, 0.2);
		box-shadow:  0px 2px 2px 0px rgba(0, 0, 0, 0.2);
	}

	.speeddial-top-middle, .speeddial-top-left,.speeddial-top-right, .speeddial-middle-left,.speeddial-middle-middle,.speeddial-middle-right, .speeddial-bottom-left,.speeddial-bottom-middle,.speeddial-bottom-right {
		display:none;
	}
	
	#speeddialWindow {
        background-image: url(http://lh5.ggpht.com/bosDZkBJxNdwo-dXGZeBkYtfCVnTFq96zqC08UV4dmIccI4YBr5p0CyCE7vmj2w=w9999-h9999);
        background-repeat: no-repeat;
        background-position: center center;
        background-size: auto 100%;	
	}

	.speeddial-container{
		margin:0 !important;
		border:0 !important;
		background-color:  transparent  !important;
	}

}
