css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("klavogonki.ru") {

/* Скрытие рекламы */
.banner-back {display: none !important;}
#playads {display: none !important;}
#playads_dummy {display: none !important;}

/* Убрать строку о статусе аккаунта и надпись «Активировать Премиум» в профиле */
#premium-dl {display: none !important;}

/* Скрыть покупку очков за реальные деньги */
#buy-scores-link {display: none !important;}

/* Скрыть функции в профиле, которые доступны только для Премиум */
#modeform {display: none !important;}
#startdateform {display: none !important;}
#errors-stats-link {display: none !important;}

/* Скрыть Премиум-работу над ошибками в конце заезда */
#errorwork {display: none !important;}

/* Спрятать функцию «Отменить результат» в конце заезда */
.delresult {display: none !important;}

}
