css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mcft.net") {
body {
    background: none repeat scroll 0% 0% #bfdcef !important;
}
.Comment .Meta .no-stile {
    background: linear-gradient(to right, rgba(207, 236, 255, 0.87) 0%, rgba(207, 236, 255, 0.87) 55%, rgba(207, 236, 255, 0) 100%) repeat scroll 0% 0% transparent !important;
}
body.Discussion #Content, div#Content {
    background: none repeat scroll 0% 0% rgb(207, 236, 255) !important;
    box-shadow: 0px 0px 5px #000 !important;
    border: 1px solid #bfdcef !important;
}
.Preview .Message, .Preview .Message p, .MessageList .Message {
    background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.35) !important;
    border-top: 2px solid rgba(0, 0, 0, 0.07) !important;   
    border-bottom: 2px solid  rgba(255, 255, 255, 0.40) !important;
}
.DataList .Meta strong {
    border-radius: 2px 2px 2px 2px;
    background: -moz-linear-gradient(center top , #ffff5d, #efef00) repeat scroll 0% 0% transparent !important;
    color: rgb(0, 0, 0) !important;
    border: 1px solid #ffff00
}
a.Bookmark, a.Bookmarked, a.Bookmarking {
    background: none no-repeat scroll center top transparent !important;
}
a.Bookmark:after {
    content: "★";
    font-size: 22px;
    color: #fff;
}
a.Bookmarked:after {
    content: "★";
    font-size: 22px;
    color: #ffff5d;
}
a.Bookmarking:after {
    content: "★";
    font-size: 22px;
    color: #aaaaff;
}
.PanelInfo li:hover {
    background: none repeat scroll 0% 0% rgb(240, 245, 255) !important;
}
}
