css:
@-moz-document  url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')  {

.bluthSkin .mceIcon {
  background-image: url('http://25.media.tumblr.com/0ec452905bff3178b93e01d6f75707ba/tumblr_mlvrh2WLqH1r39deko1_500.png') !important;
}

.main_content,
.bluthSkin table.mceLayout tr.mceFirst td,
.bluthSkin .mceIframeContainer,
#video_caption,
.main_content.photo_form .inner_content,
.tag_editor,
.main_content.photo_form .tags,
textarea.preview,
input.bordertop, textarea.bordertop,
.rounded_border_group,
.video_option:not(:last-child),
.audio_tabs li,
img.mcepagebreak
{
  border-color: #444 !important;
}

#post_controls button.close,
#tumblelog_select .txt,
#post_header .reblog_source a,
.tag_editor,
.advanced_post_options label,
.advanced_post_options input[type="text"],
.popover_header,
.photo_form .webcam_button,
.photo_form .url_button,
.audio_tabs li a
{
  color: #444 !important;
}

.reblog_select
{
  background-color: #444 !important;
}

.drop_zone,
#post_controls button.close,
.photo_form .webcam_button,
.photo_form .url_button
{
  border-color: #999 !important;
}

.photo_form .webcam_button,
.photo_form .url_button
{
  font-size: 100%;
  font-weight: bold;
  border: 1px solid;
  width: 33%;
  padding: 5px 0;
  border-radius: 3px;
}

.audio_tabs li a
{
  background-color: #ddd;
}

#post_header .reblog_source
{
  opacity: 1.0 !important;
}

.bluthSkin .mceButtonDisabled .mceIcon
{
  opacity: 0.5 !important;
  filter: alpha(opacity=50) !important;
}

.drop_zone .help,
label.optional,
.inset_label .label,
#post_form .advanced_post_options .option input.placeholder,
#post_form .placeholder
{
  color: #777 !important;
}

div.l-content .white_pane ::-webkit-input-placeholder,
div.l-content .full_answer_container ::-webkit-input-placeholder
#post_form .inplace_edit::-webkit-input-placeholder,
#post_form .advanced_post_options .option input::-webkit-input-placeholder,
#post_form ::-webkit-input-placeholder
{
  color: #777 !important;
}

div.l-content .white_pane :-moz-placeholder,
div.l-content .full_answer_container :-moz-placeholder,
#post_form .inplace_edit:-moz-placeholder,
#post_form .advanced_post_options .option input:-moz-placeholder,
#post_form input:-moz-placeholder,
#post_form textarea:-moz-placeholder
{
  color: #777 !important;
}

}
