css:
@-moz-document url-prefix('http://forums.firefallthegame.com/') {
    body{
        background-position: center top !important;
        background: rgb(14, 14, 16) !important;
    }
    
    #ff-nav-base {
        height: 68px !important;
        background: rgb(14, 14, 16) !important;
    }
    .discussionListItem, .contentM, .lastPost, .stats, .subHeading {
        background: rgb(14, 14, 16) !important;
    }
    .headerL, .headerR, .headerC, .contentR, .footL,
    .footC, .footR, .navTabs, .navTab, .navLink, .pageWrapper,
    .tabLinks{
        background: rgb(14, 14, 16) !important;
    }
    .likesSummary, .discussionList .sectionHeaders, .titleBar,
    .titleLeft, .titleRight, .node .nodeLastPost,
    .nodeList .categoryStrip, .catRight, .catLeft{
        background: none !important;
    }
    
    .titleBar{
        border: 1px dashed white;
        border-radius: 5px;
    }
    
    .PageNav a, a.callToAction span{
        background-image: none !important;
        background: #123 !important;
    }

    .nodeList .categoryStrip{
        border: 1px solid #FFF;
    }
    
    .PageNav a.currentPage{
        background: #678 !important;
    }
    
     .PageNav a:hover{
        background: #456 !important;
    }
}
