css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.reddit.com") {

body
{
	font: normal 10px helvetica, verdana, arial, sans-serif;
	background-color: white;
}

.tabmenu li {font-weight: normal;}
.tabmenu li.selected a
{
	color: #de4040;
	font-size: 20px;
}

.md {font-size: 10px;}

.link
{
	border-bottom: 1px solid #DDD;
	padding-bottom: 2px;
}

a, .thing .title, .thing .title.loggedin {color: #4054cb;}

a:visited,
.thing .title.loggedin.click,
.thing .title.loggedin.click:visited,
.thing .title:visited,
.thing .title.loggedin:visited
{
	color: #c58014;
}

.tagline, .tagline a {color: #999;}

.entry .buttons li.nsfw-stamp.stamp {padding-right: 0;}

li.nsfw-stamp acronym
{
	color: #fff;
	background-color: #de4040;
}
.entry .buttons li a
{
	color: #ccc;
	font-weight: normal;
}

.entry .buttons li.first a.comments
{
	color: #de4040;
	font-weight: normal;
}
.nextprev a
{
	font-weight: normal;
	font-size: 20px;
}
.thing.comment
{
	padding: 10px 10px 2px 10px;
	border: 1px solid #ccc;
	margin-bottom: 10px;
}

.thing .title {font-size: 14px;}
.thing p.parent {padding-bottom: 5px;}

}
