css:
@-moz-document domain("niburu.nl") {
body, td, th, div, p, label, input,
html #content {
  background-color: #fff !important;
  background: none !important;
  font-size: 16px;
  box-shadow: none;
  line-height: 26px;
}

.article-title-text {
  text-shadow: none;
  font-family: sans-serif;
  font-size: 32px !important;
  line-height: 38px !important;
  }

iframe {
  width: 100%;
}

.contentpaneopen p {
  font-family: serif;
  font-size: 16px;
  line-height: 28px;
  }

#content,
.header-slogan-nieuws {
  box-shadow: none;
  padding: 1px 28px 0 0;
  
  }

#left,
.donatietop,
.donatietopimage,
.header-slogan-nieuws {
  display: none;
  }
    

.container-indent {
	padding: 19px 33px 0 0;
}
}
