css:
@-moz-document domain("king3.de") {
img[src*="http://www.king3.de/forum/images/header.gif"] {
  content: url('http://img805.imageshack.us/img805/1792/king3f.png');
  height: 200px;
}
  
.jqueryslidemenu, .jqueryslidemenu ul li a {
    background: #222222 !important;
}

.jqueryslidemenu ul li a:hover {
    background: #8c0808 !important;
}
    
.thead {
    background: #181818 !important;
	background: -moz-linear-gradient(top,  #181818 0%, #606060 100%) !important;
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#181818), color-stop(100%,#606060)) !important;
	background: -webkit-linear-gradient(top,  #181818 0%,#606060 100%) !important;
	background: -o-linear-gradient(top,  #181818 0%,#606060 100%) !important;
	background: -ms-linear-gradient(top,  #181818 0%,#606060 100%) !important;
	background: linear-gradient(to bottom,  #181818 0%,#606060 100%) !important;
	filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#181818', endColorstr='#606060',GradientType=0 ) !important;
}

.tfoot {
  	background: #606060 !important;
	background: -moz-linear-gradient(top,  #606060 0%, #181818 100%) !important;
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#606060), color-stop(100%,#181818)) !important;
	background: -webkit-linear-gradient(top,  #606060 0%,#181818 100%) !important;
	background: -o-linear-gradient(top,  #606060 0%,#181818 100%) !important;
	background: -ms-linear-gradient(top,  #606060 0%,#181818 100%) !important;
	background: linear-gradient(to bottom,  #606060 0%,#181818 100%) !important;
	filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#606060', endColorstr='#181818',GradientType=0 ) !important;
}
  
.tcat {
    background: #606060 !important;
}
    
#panel {
    background: #efefef !important;
	background: -moz-linear-gradient(top,  #efefef 0%, #cccccc 100%) !important;
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#efefef), color-stop(100%,#cccccc)) !important;
	background: -webkit-linear-gradient(top,  #efefef 0%,#cccccc 100%) !important;
	background: -o-linear-gradient(top,  #efefef 0%,#cccccc 100%) !important;
	background: -ms-linear-gradient(top,  #efefef 0%,#cccccc 100%) !important;
	background: linear-gradient(to bottom,  #efefef 0%,#cccccc 100%) !important;
	filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#efefef', endColorstr='#cccccc',GradientType=0 ) !important;
}

.trow_sep {
	background: #aaaaab !important;
	padding: 2px;
}
  
.quote_header {
    background: #181818 !important;
	background: -moz-linear-gradient(top,  #181818 0%, #606060 100%) !important;
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#181818), color-stop(100%,#606060)) !important;
	background: -webkit-linear-gradient(top,  #181818 0%,#606060 100%) !important;
	background: -o-linear-gradient(top,  #181818 0%,#606060 100%) !important;
	background: -ms-linear-gradient(top,  #181818 0%,#606060 100%) !important;
	background: linear-gradient(to bottom,  #181818 0%,#606060 100%) !important;
	filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#181818', endColorstr='#606060',GradientType=0 ) !important;
}
    
hr {
    width: 50% !important;
}
}
