css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wiki.guildwars.com") {
body, td, dt
{
border: none !important;
background: #222222 !important;
}
table, div#content, div#column-content
{
background: #222222 !important;
}
div, ul
{
background: transparent !important;
}

td, h1, span, h5, p, li, i, div.thumbcaption, div#latest-update, div#content, h2
{
color: white !important;
}
div.thumb.tright, div.thumbinner, div.thumb.tleft
{
border-color: #222222 !important
}
div
{
border-color: #999999 !important;
}
a
{
color: gold !important;
}
a:visited
{
color: #A329CC !important;
}
}
