@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("someimage.com") {

/* Hides Header */
#header { display: none !important; }

/* Hides Gallery Link */
.gallerylinkwhite { display: none !important; }

/* Hides "Share Code" */
.midbox { display: none !important; }

/* Hides "Footer" */
#dockedbar { display: none !important; }

/* muahah too easy - removes all the ads */
iframe { display:none !important; }

}
