css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

/*replaces photo thumbnails*/
.uiPhotoThumb.photoRedesignAspect   {
    box-shadow: 3px 3px 3px #333333;
}

/*replaces share boxes*/

.clearfix.shareRedesignContainer    {
    box-shadow: 2px 2px 2px #AAAAAA;
}

/*replaces comment boxes*/

.uiUfi.UFIContainer    {
    box-shadow: 5px 5px 5px #CCCCCC;
}

/*replaces share boxes*/

.fbPhotoSnowliftContainer.uiContextualLayerParent   {
    box-shadow: 15px 15px 15px #333333;
    border: 1px solid #222222
}

/*replaces video thumbnails*/

.uiVideoThumb   {
    box-shadow: 3px 3px 3px #333333;
}

/*replaces status boxes*/

.fbTimelineUnit.fbTimelineTwoColumn.clearfix    {
    box-shadow: 5px 5px 5px #CCCCCC!important;
}

/*replaces cover photos*/

#fbCoverImageContainer.coverWrap    {
    box-shadow: 5px 5px 5px #CCCCCC!important;
}

/*replaces toolbar below cover photo*/

#fbTimelineHeadline.clearfix    {
    box-shadow: 5px 5px 5px #CCCCCC!important;
}

/*replaces profile picture*/

.profilePic.img  {
    box-shadow: 2px 2px 2px #555555!important;
    border: 1px solid #111111
}

/*replaces toolbar boxes*/

#blueBar.fixed_elem {
    box-shadow: 2px 2px 2px #3B5998!important;
    border: 1px solid #111111
}

/*replaces scaled image boxes*/

.uiScaledImageContainer.photoWrap   {
    box-shadow: 3px 3px 3px #333333;
}

/*replaces cover image share boxes*/

.uiPhotoThumb.photoRedesignCover {
    box-shadow: 3px 3px 3px #333333;
}

}
