css:
@-moz-document domain("forums.firefallthegame.com") {
#ff-contleftcol > DIV > DIV:first-child {
  display: none !important;
}


div#navigation {
  margin-top: 20px;
}
}

@-moz-document url("http://forums.firefallthegame.com/community/account/alerts"), url-prefix("http://forums.firefallthegame.com/community/categories/"), url-prefix("http://forums.firefallthegame.com/community/forums/"), url-prefix("http://forums.firefallthegame.com/community/threads/"), url-prefix("http://forums.firefallthegame.com/community/watched/") {
#content > DIV > DIV > DIV:first-child > DIV:nth-child(2) {
  display: none !important;
}


#ff-contrightcol > DIV > DIV > DIV {
  position: relative;
  z-index: 100;
}
}

@-moz-document url("http://forums.firefallthegame.com/community/") {
div.mainContent {
  margin-top: -140px;
}
}

@-moz-document url-prefix("http://forums.firefallthegame.com/community/categories/") {
#content > DIV > DIV > OL > LI:first-child > DIV:first-child {
  display: none !important;
}
}

@-moz-document url-prefix("http://forums.firefallthegame.com/community/forums/") {
#content > DIV > DIV > DIV:first-child {
  margin-top: -145px;
}

#content > DIV > DIV > DIV:nth-child(3) {
  margin-top: -50px;
}

#content > DIV > DIV > DIV:nth-child(2) > DIV > A {
  margin-left: -520px;
}

div.titleBar {
  width: 1000px;
}
}

@-moz-document url-prefix("http://forums.firefallthegame.com/community/threads/"), url-prefix("http://forums.firefallthegame.com/community/account/"), url-prefix("http://forums.firefallthegame.com/community/categories/"), url-prefix("http://forums.firefallthegame.com/community/watched/") {
div.titleLeft {
  margin-top: -100px;
}
}
