css:
@-moz-document url-prefix("http://www.reddit.com/r/leagueoflegends"), url-prefix("http://dd.reddit.com/r/leagueoflegends") {

/* Comment downvote warning */
.comment .down:hover:before {
    content: "Only downvote comments that do not contribute to discussion, not those with which you disagree.";
    position: absolute;
    width: 220px;
    z-index: 99;
    
    margin: -28px 0 0 24px;
    padding: 3px;
    
    background-color: #0C2E2D;
    
    border: 2px solid #3C5E5D;
    border-radius: 5px;
    
    color: white;
    font-size: x-small;
    font-weight: bold;
    text-align: center;
}

/* Arrow opacity */
.arrow.down {
    opacity: 0.5;
}

.comment .down:hover {
    opacity: 1.0; /* Fixes downvote hover not appearing */
}

}
