css:
@-moz-document domain("www.upworthy.com") {
    div#sidebar-content h3.title {
        margin-top: 30px !important;
    }
    div.sticky-header.visible,
    div.survey-iframe-wrap {
        display: none !important;
    }
    div#sidebar-content div.info {
        border: none !important;
    }
    .modal-open {
        overflow-y: scroll;
    }
    #slider,
    #attributionRow,
    #fbSlider,
    #shareOverlay,
    div.holler,
    div.holler *,
    div.modal-backdrop,
    #shareTop,
    #shareRowBottom,
    #footerEmailSignup,
    #suggestedSlider,
    div#holler,
    .dfp-show-after-render {
        display: none !important;
    }
    #rc-header-container {
        box-shadow: none !important;
        border-bottom: none !important;
    }
    #rc-header-container:before {
        height: 0px !important;
        background: transparent !important;
    }
    h1 {
        font-size: 28px !important;
    }
    body,
    #wrapper,
    #main-header,
    #container,
    #navMenuFooter,
    aside,
    footer,
    #footer-email-signup,
    #nuggetPage aside#sidebar #recommended .promoted small,
    #nuggetPage aside#sidebar #recommended .promoted h3,
    section#clickability .slide-content,
    div.unified-nav,
    div.meta-nav,
    #upworthy-web-app-wrapper,
    header #byline address,
    div.fluid-page,
    .story-teaser__author,
    .fact-check-toggle,
    .background--white,
    div.sticky-nav,
    div.stick-nav--visible {
        background: #080808 !important;
        border: none !important;
        box-shadow: none !important;
        color: #ccc !important;
    }
    .fact-check-toggle {
        top: 4px !important;
    }
    #subscribe-facebook,
    .footer-subscribe {
        display: none !important;
    }
    .story-teaser--with-lede {
        background: #080808 !important;
        color: #eee !important;
    }
    #navMenuFooter {
        margin-top: -20px !important;
        font-size: small !important;
    }
    .topics-list {
        height: auto !important;
        border-color: transparent !important;
        margin: 0 !important;
    }
    html * {
        font-family: "Source Sans Pro", "Lucida Grande", Verdana, "Avenir Next", "Trebuchet MS", "Helvetica Neue", Helvetica, Arial, sans-serif !important;
    }
    div.nav-container,
    div#branding,
    .nugget-preview__eyecatcher,
    .section-title span,
    .section-title::before {
        background-color: transparent !important;
        border: none !important;
        color: #eee !important;
    }
    a#logo {
        opacity: 0.7 !important;
        transform: scale(0.8, 0.8) !important;
        margin-top: 0px !important;
    }
    .wrapper #header-logo-container {
        border: none !important;
    }
    img,
    .eyecatcher {
        border: 1px solid #111 !important;
        border-radius: 2px !important;
        background: #000 !important;
    }
    #main-header,
    .article-metadata {
        background: transparent !important;
    }
    div.container,
    div.info {
        background: #080808 !important;
        color: #eee !important;
        margin-top: -20px !important;
    }
    blockquote,
    h2,
    h3 {
        background: transparent !important;
        color: #fff !important;
    }
    section#clickability .pkg {
        box-shadow: none !important;
        border: 1px solid #555 !important;
    }
    div.share-button-wrapper {
        opacity: 0.7 !important;
    }
    a {
        color: #38f !important;
        text-shadow: none !important;
    }
    hr {
        border: 3px #444 !important;
    }
    li.loaded {
        background-color: #050505 !important;
        border-top: 1px solid #222 !important;
        border-bottom: 1px solid #222 !important;
        border-left: none !important;
        border-right: none !important;
        box-shadow: none !important;
    }
    .nugget-share--bottom::after {
        display: none !important;
    }
    ul#recommended-footer {
        background-color: #111 !important;
        color: #eee !important;
        border-top-color: #555 !important;
    }
    #page-footer .copyright-info,
    #page-footer .nav-wrap {
        border-right-color: transparent !important;
        border-left-color: transparent !important;
    }
    a[data-reactid] {
        margin-top: 20px !important;
    }
    li.loaded:first-child {
        border-top: none !important;
    }
    ul#recommended-header,
    .nugget-share--bottom .nugget-share__text-link {
        border: 1px solid #111 !important;
        border-radius: 3px !important;
        background: #050505 !important;
    }
    ul#recommended-header .info {
        margin-top: 0px !important;
    }
    ul#recommended-header li {
        border: none !important;
    }
    article,
    article dl {
        border: none !important;
        background: #0a0a0a !important;
    }
    article dl {
        font-size: 13px !important;
    }
    article {
        border: 1px solid #222 !important;
        border-radius: 3px !important;
        padding-left: 15px !important;
        padding-right: 15px !important;
        padding-top: 5px !important;
        padding-bottom: 0px !important;
        margin-top: 10px !important;
        font-size: 15px !important;
    }
    #transcriptContent {
        color: #aaa !important;
        background: #080808 !important;
        box-shadow: none !important;
        border: 1px solid #555 !important;
    }
    #transcriptHide,
    #transcriptShow {
        background: #222 !important;
        color: #ace !important;
    }
    ul.dropdown-menu,
    ul.dropdown-menu * {
        background-color: #222 !important;
        border-color: #555 !important;
    }
    ul.dropdown-menu {
        border-radius: 5px !important;
    }
    a.btn {
        color: white !important;
    }
    a.btn.close {
        background-color: #333 !important;
        background-image: none !important;
    }
    body #header-logo-container #logo {
        min-height: 80px !important;
    }
    #clickability {
        background-color: transparent !important;
    }
    .slide-content {
        border-color: #555 !important;
    }
    #nuggetPage section#footer-youtube-subscribe {
        background: transparent !important;
    }
    div#subscribe-top,
    div#share-top,
    div#sticky-header,
    a#footer-stamp,
    div#promoted-post-engagement-box,
    header.sponsored {
        display: none !important;
    }
    div.fluid-width-video-wrapper iframe {
        border: 1px solid #222 !important;
    }
    input[type="email"],
    input[type="text"],
    input[type="password"] {
        background-color: #222 !important;
        color: #eee !important;
        border-color: #444 !important;
    }
    input[type="submit"],
    input[type="submit"]:hover,
    input[type="submit"]:focus {
        background-image: none !important;
        background-color: #a33600 !important;
        border-color: #b44700 !important;
    }
    #attribution,
    #footer,
    #meta-data,
    #transcript {
        border-top-color: #222 !important;
        border-bottom-color: #222 !important;
    }
    #primary-nav #branding {
        box-shadow: none !important;
    }
    #primary-nav #branding:before {
        background-image: none !important;
    }
    #page-footer .social li a {
        background-color: #222 !important;
        border-color: #444 !important;
    }
    #page-footer #logo-circle {
        background: #222 none repeat scroll 0% 0% !important;
    }
    div.EmbeddedTweet-tweet {
        background: #222 !important;
        padding: 3px 10px !important;
        margin: 0px !important;
    }
    div.story-preview,
    div.article-left-column__item {
        background: #222 !important;
        border-radius: 5px !important;
    }
}
