css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thinkery.me") {

    body, select, input, textarea {
        color: #fff !important;
        background-color: #333 !important;
    }

    header {
        background: linear-gradient(to bottom, #666 0%,#333 100%) !important;
    }

    .shadow {
        box-shadow: 0px 0px 4px #222 !important;
    }
    
    #search-input {
        border: 1px solid #999 !important;
        background: #333 !important;
    }
    section#things ul li.active,section#things ul li.archived.active, nav#menu ul li.active, nav#menu ul li:hover {
        background-color: #889 !important;
    }
    section#things ul li.active article,section#things ul li.archived.active article{
        color: #fff !important;
    }
    section#things ul li.hover {
        background-color: #99a !important;
    }
    ul.tags.all li {
        background-color: #889 !important;
    }
    ul.tags.all li small {
        color: #fff !important;
    }

    .grey {
        color: #ccc !important;
    }

    section#things ul li article a, section#things ul li section#flyout .content h1 a, nav#menu a {
        color: #bbe !important;
    }

    ul.tags.all li a {
        color: #fff !important;
    }
    
    section#things ul {
        border-right: 1px solid #444 !important;
        border-left: 1px solid #444 !important;
        box-shadow: 3px 3px 3px #555 !important;
    }
}
