@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("antyweb.pl") {
    
    #cover,
    #menu-wood {
        display: none;
    }
    
    #entry-cover-big {
        height: 300px;
    }

    #single-entry .entry-cover {
        max-height: 300px;
        overflow: hidden;
        -webkit-transition: max-height 1s ease-in-out;
           -moz-transition: max-height 1s ease-in-out;
                transition: max-height 1s ease-in-out;
    }
    
    #single-entry .entry-cover:hover {
        max-height: 9999px;
        overflow: hidden;
        -webkit-transition: max-height 1s ease-in-out;
           -moz-transition: max-height 1s ease-in-out;
                transition: max-height 1s ease-in-out;
    }
}
