css:
@-moz-document url-prefix('https://class.coursera.org/') {

@media print {

body { background-color: #fff; background-image: none; }

.coursera_topbar { display: none; }

#banner-top { display: relative; }
.course-logo-image { top: 6px !important; }
.course-logo-name { top: 47px !important; }

.sidebar { display: none; }

.breadcrumb { position: relative; z-index: 99; }

.container-fluid { min-width: 0 ; }
.container-fluid > .content { margin-left: 0; }

iframe[allowfullscreen] { display: none; }
iframe[allowfullscreen] + *:before { display:block; content:"Video removed for printing"; font-size: 13px; line-height: 1.384615385em; font-weight: normal; margin: 0 0 9px; }

#AssessmentNavigationView { display: none; }
#assessment-frame { position: relative; margin-top: -2em; min-width: 0 !important; }
#topContentToolbar { display: none !important; }
.assessmentSection > .assessmentExposition { padding-top: 0; }

.assessmentSection > .assessmentExposition:first-child { padding-bottom: 0; }
.assessmentSection > .assessmentExposition:first-child:after { display: block; content:"Assessment questions"; font-size: 20.4px; line-height: 36px; font-weight: bold; text-rendering: optimizelegibility; border-top: 1px dashed #ccc; padding-top: 9px; }

.evaluationGroup, .assessmentAnswer { display: none; }

#assessmentFormContent > .alert { display: none; }
#bottomContentToolbar { display: none !important; }

}

}
