css:
@namespace url(http://www.w3.org/1999/xhtml); 
 
@-moz-document url-prefix("http://forum.square-enix.com/ffxiv/") { 
  html { background-image: none !important; }
  body { margin: 38px 0px 0px 0px !important; width: 100% !important;  }
  ul.floatcontainer > li > a:link { color: rgb(69, 95, 47) !important; }
  ul.floatcontainer > li > a:visited { color: rgb(69, 95, 47) !important; }  
  ul.floatcontainer > li > a:hover { color: rgb(166, 191, 0) !important; }  
  ul.floatcontainer > li > a:active { color: rgb(166, 191, 0) !important; }    
  div.navbar > div.globalsearch > div > a:link { color: rgb(69, 95, 47) !important; }
  div.navbar > div.globalsearch > div > a:visited { color: rgb(69, 95, 47) !important; }  
  div.navbar > div.globalsearch > div > a:hover { color: rgb(166, 191, 0) !important; }  
  div.navbar > div.globalsearch > div > a:active { color: rgb(166, 191, 0) !important; }    
  .above_body { background: none !important; background-color: #F0F0EE !important;  }
  .above_body > div > a { display: none !important; }
  .body_wrapper { background-image: none !important; background-color: #F0F0EE !important; }
  body > div.above_body + div { background: none !important; display: none !important; }
  body > img { display: none !important; }
  div#navbar { margin: 0px 0px 0px !important; }
  div.below_body > table { width: 80% !important; color: #000 !important; margin: auto auto !important; }
  .block.vbform { width: 100% !important; max-width: 100% !important; }
  .bbcode_container { width: 100% !important; margin: 0 auto !important; }  
  .bbcode_quote.withavatar { margin-left: 85px !important;}
  div.threadlisthead.table > div > span.threadinfo { width: 40% !important; }
  div.threadlisthead.table > div > span.threadlastpost { float: right !important; text-align: right !important; margin-right: 10px !important; }  
  .threadbit > div > div > div > table > tbody > tr > td:nth-child(2) { width: 40% !important; }
  .threadbit > div > div > div > table > tbody > tr > td:nth-child(3) { width: 20% !important; }
}
