css:
@-moz-document domain('trello.com') {

.action-comment,
.new-task.focus .new-task-text,
.window-main-col .edit textarea.field,
.action-comment textarea,
.list-card, .list-header,
.new-comment.focus .new-comment-input,
.task-list, .window-header {
direction: rtl;
}

.checklist-title,
.checklist-item-details-text,
.checklist-item-row.js-checkitem-row.current.hide-on-edit,
textarea.checklist-new-item-text.js-new-checklist-item-input {
  text-align:right;
  direction:rtl;
}

.list-card-operation {
  	left: 3px;
    right: auto;
}

.checklist-item-checkbox {
	left: auto;
    right: 5px;
}

.editing .edit {
    width: 93%;
}

.window-module-title-options {
    float: left;
}

input#labelName {
    direction: rtl;
}

.window-main-col .edit textarea.field {
    overflow: auto;
}

textarea.comment-box-input.js-new-comment-input {
    direction: rtl;
}

}
