css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.wikipedia), url-prefix(http://en.wikipedia)

{


h1, h2, h3, h4, h5, h6
{
    border-bottom: 1px solid #DADADA;
}

h1
{
    padding-bottom: 0 !important;
    margin-bottom: 0 !important;
}
h2
{
    font-size: 160% !important;
}

.firstHeading
{
    font-size: 28pt !important;
    font-family: "Lucida Grande" ,Tahoma,Verdana,times,serif;
    padding-bottom: 5px !important;
    color: #444 !important;
}

#p-cactions, #p-logo, #p-tb, #p-lang
{
    display: none !important;
}
body
{
    background: #dddddd !important;
}

p, ul, dd, dt
{
    margin-bottom: 1em !important;
}

.dablink
{
    padding-left: 0em !important;
    opacity: 0.5;
}

.dablink:before
{
    content: "| ";
}

#content
{
    margin: 10pt 10pt 10pt 10pt !important;
    border: solid #aaaaaa 1px !important;
    padding: 0px 25px 30px 25px !important;
}

.portlet, #footer, #bodyContent > H3:first-child + DIV + DIV + DIV + TABLE, #bodyContent > H3:first-child + DIV + DIV + DIV + TABLE + TABLE, #anon-banner, #siteSub, #siteNotice
{
    display: none !important;
}

.metadata.plainlinks.ambox.ambox-content
{
    margin-top: 10px !important;
    margin-bottom: 10px !important;
}

table.ambox th.ambox-text, table.ambox td.ambox-text
{
    padding-left: 1em !important;
    padding-right: 1em !important;
    width: 100%;
    opacity: .75;
}

.searchresult
{
    width: 95% !important;
}
.infobox
{
    margin-left: 1.5em !important;
    margin-top: 1.0em !important;
}
.thumbinner
{
    margin-left: 1.5em !important;
    margin-top: 1.0em !important;
}

#toc #toctitle, .toc #toctitle, #toc .toctitle, .toc .toctitle
{
    text-align: left !important;
    font-weight: normal !important;
    font-size: 85% !important;
}

}
