css:
@-moz-document domain('startpage.com')
{
  body
  {
    background: #000 !important;
    color: #DDD !important;
    margin:0;
    padding:0;
  }
  
  div#search, div#results_header, div#results
  {
    margin:0 !important;
    padding:0 !important;
  }
  
  div#head, div#logo, div#enhanced, a#detail,
  div#side_bar, div#show_hide, div#tip, p#results_count_p,
  a#proxy_link, a.proxy,
  tr#bottom_input_p, td#within, div#footer_links 
  {
    display:none !important;
  }
  
  p
  {
    color:#000 !important;
  }
  
  div.result
  {
    padding:1em !important;
  }
  
  span.url
  {
    color:#AEAEAE !important;
  }
  p.desc
  {
    color:#EEE !important;
  }
  
  
  a,a:link
  {
    color:#FAFAFA !important;
    font-weight:bold !important;
    text-decoration:none !important;
  }
}
