css:
@-moz-document url-prefix("http://hani.co.kr"), url-prefix("http://www.hani.co.kr"), url-prefix("http://hani.com"), url-prefix("http://www.hani.com") {

* {
    background-color: #000 !important;
    border-color: #262626 !important;
    color: #9a9a9a !important;
}
a {
    color: #000000 !important;
}
a:visited {
    color: #ccc !important;
}
a:link {
    color: #ccc !important;
    /*font-weight: bold !important;*/
}
a:hover,
a:hover span {
    color: #fff !important;
    text-decoration: underline !important;
}
#main-top {
    background: inherit;
}
#header,
#footer,
#sitemap,
.title,
.tab-menu,
.theme-title,
.gnb-inner,
.btn.prev,
.btn.next,
#snews-prev,
#snews-next,
.btn-set {
    opacity: 0.6;
    filter: alpha(opacity=60);
    -moz-opacity: 0.60;
}
.move-theme {
    background: #000 !important;
    border-bottom: 1px dotted #262626 !important;
}
.weather-info {
    background: #000 !important;
}
.ad-banner,
.ad-banner1,
.ad-banner2,
#visual-ad,
.visual-ad1,
.visual-ad2,
#advertise-right-quick,
#ad-insurance,
#power-link-ad,
#power-link2,
#premium-link,
.overflow-ad,
.hani-advertise_280_250,
#ad_hani9,
#about-shopping-box,
#advertise {
    opacity: 0.2;
    filter: alpha(opacity=20);
    -moz-opacity: 0.20;
}

}
