css:
@-moz-document domain("plus.google.com") {
#contentPane div[role="main"] > div[jsmodel] > div:first-of-type,
#contentPane div[role="main"] > div[jsaction] > div:first-of-type,
#contentPane > div[jsaction] > div[role="main"] > div[jsaction] > div:first-of-type {
  width: 1050px !important;
}

#contentPane div[data-nid] {
  width: 900px !important;
}

#contentPane .ozIdStreamHomepage {
  margin: 0 auto !important;
}

#contentPane .ozIdStreamHomepage > div {
  width: 1050px !important;
}

#contentPane div[guidedhelpid="streamcontent"] {
  width: 900px !important;
}

#contentPane div[guidedhelpid="streamcontent"] > div > div:not([id^="update"]) {
  width: 900px !important;
}

#contentPane div[guidedhelpid="streamcontent"] > div > div:not([id^="update"]) > div:not([id^="update"]) {
  width: 900px !important;
}

#contentPane div[guidedhelpid="sharebox_launcher"] > div {
  width: 858px !important;
  max-width: 858px !important;
}
}

@-moz-document regexp("https://plus.google.com(/u/\\d+)?/s/") {
#contentPane div[guidedhelpid="streamcontent"] > div:nth-child(2) > div:not([id^="update"]) > div:not([id^="update"]) {
  width: auto !important;
}
}
