css:
@-moz-document domain("plus.google.com") {
/* Change font - based on code by tvierling */
* { font-family: Helvetica, Arial, sans-serif; font-size: 14px; }
div.editable { font-family: Helvetica, Arial, sans-serif;  font-size: 14px;}
.VC { font-family: Helvetica, Arial, sans-serif;  font-size: 14px;}

/* Show full posts and full comments */
.DIcL3e,.Gm {
  max-height:none!important
}
/* Remove "read more" */
.syxni.JBNexc { /* stream */
  display:none;!important
}
.JBNexc.a-n { /* profile posts */
  display:none;!important
}
.syxni.unQkyd { /* comments */  
  display:none;!important
}

/* Almost hides animated GIFs */
img.MAvK1e.lg7oNe[src$=".gif"] {
  /*You can also use display: none;*/ 
  opacity:0.05;
}

/* wider columns - based on code by Amber Yust */
#contentPane > div[jsaction] > div:last-child > div:first-of-type,
#contentPane > div > div[jsaction] > div:last-child > div:first-of-type {
  width: 1250px !important;
}

#contentPane div[guidedhelpid="streamcontent"] {
  width: 1100px !important;
}

#contentPane div[guidedhelpid="streamcontent"] > div > div:not([id^="update"]) {
  width: 1100px !important;
}

#contentPane div[guidedhelpid="streamcontent"] > div > div:not([id^="update"]) > div:not([id^="update"]) {
  width: 500px !important;
}

#contentPane div[guidedhelpid="sharebox_launcher"] > div {
  width: 468px !important;
  max-width: 468px !important;
}


#contentPane div[data-nid] {
  width: 500px !important;
}

#contentPane > div > div[tabindex="-1"] > div > div[style] {
  width: 500px !important;
}

}
