css:
@-moz-document domain("twitter.com") {
div.dashboard {
    position: fixed!important;
  
}

div.wrapper.wrapper-home.black {
    background-color: #468!important;
}

div.new-tweets-bar.js-new-tweets-bar {
    color: #ffffff!important;
    background-color: #446688!important;
    font-weight: bold!important;
}


img.avatar.js-action-profile-avatar {
    border-radius: 100px!important;
}

img.avatar.js-action-profile-avatar {
    border-radius: 100px!important;
}

img.avatar.size32 {
    border-radius: 9px!important;
}

i.nav-new {
    border-bottom: 3px solid #20aaf5!important;
    -moz-transition-duration: 0.2s!important;
    height: 1.5px!important;
    background: none!important;
}

div.global-nav {
    background: #000!important;
}


h1.icon.bird-topbar-etched {
    display: none!important;
}

.people:hover {
    border-bottom: 3px solid #20aaf5!important;
    -moz-transition-duration: 0.2s!important;
    height: 36.5px!important;
}

#global-nav-home:hover {
    border-bottom: 3px solid #20aaf5!important;
    -moz-transition-duration: 0.2s!important;
    height: 36.5px!important;
}

.topics:hover {
    border-bottom: 3px solid #20aaf5!important;
    -moz-transition-duration: 0.2s!important;
    height: 36.5px!important;
}

.profile:hover {
    border-bottom: 3px solid #20aaf5!important;
    -moz-transition-duration: 0.2s!important;
    height: 36.5px!important;
}

.nav .active a {
    background: none!important;
}


}
