/*
 * Vurmin.org Lightened Up
 *
 * Author            : John Doe
 * Website           : http://www.vurmin.org/
 * Description       : A lightened style for Vurmin.org.
 */
@charset "utf-8";

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
domain("vurmin.org")
{

body{
background-color: #555 !important;
}

div.codeblock,blockquote{
background-color: #555 !important;
border: 1px solid #888 !important;
}

a:link, a:active, a:visited {
color: #aaa !important;
}

textarea, input.textbox, div.editor_dropdown_label, ul.editor_dropdown_menu > li {
background: #555 !important;
color: #ddd !important;
}

#container {
width:90% !important;
}

img {
max-width: 800px;
}

}
