css:
@-moz-document regexp(".*blog.livedoor.jp/kinisoku/.*") {

  #header, #aside, *[id*="_ad"], *[class*="_ad"], #article_rss,
  #moretop_ad, #social_bottom, .all_article, a[href*="2ch-c"],
  h3, #rss_premium, .related-articles, #section_rss, *[id*="rank"],
  #form_wrap, #pagenav, #footer, address, a[href*="amazon"],
  a[href*="ads"], #popular1, .ldAdLink
  { display: none !important; }

  #article {
    float: none !important;
    margin: 2em auto;
    min-height: inherit;
  }

  .section {
    border: none !important;
    box-shadow: 0 0 3px #ddd;
  }
    
  .article_body {
    padding: 2em;
  }
  
  #comment {
    padding: 2em;
  }

}
