css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("playlist.com") {

	

	/* applies font to all generic HTML tags */

	*,

        html,

	body,

	span,

	div,

	td,

	br,

	input,

	font,

	button,

	select,

	option {

		font-family: Ar Blanca !important;

		font-size: 10pt !important;

	}


}
