css:
@-moz-document domain("github.com") {
/* ***** GitHub Source Comment Green *****
2013-5-27  v1.00
2013-5-29  v1.01
2014-11-13 v1.02
2015-1-28  v1.03
2015-7-11  v1.04
2015-7-14  v1.05
2015-7-15  v1.06
2015-7-21  v1.07
2015-10-6  v1.08
*/
/* c=comment */
.highlight .pl-c
  { color: #008400; font-style: normal; }
/* c1=constant  s=storage  v=variable */
.highlight .pl-c1,
.highlight .pl-s .pl-v
  { color: #202020; }
/* e=entity  en=entity.name */
.highlight .pl-e,
.highlight .pl-en
  { color: #202020; }
/* s=storage  s1=string */
.highlight .pl-s .pl-s1
  { color: #0000ff; }
/* smi=storage.modifier.import */
.highlight .pl-smi
  { color: #202020; }
/* ent=entity.name.tag */
.highlight .pl-ent
  { color: #0000ff; }
/* k=keyword */
.highlight .pl-k
  { color: #0000ff; }
/* pds=punctuation.definition.string
   s=storage  pse=punctuation.section.embedded  s1=string
   sr=string.regexp  cce=constant.character.escape
   sra=string.regexp.arbitrary-repetition
   sre=source.ruby.embedded */
.highlight .pl-pds,
.highlight .pl-s,
.highlight .pl-s .pl-pse .pl-s1,
.highlight .pl-sr,
.highlight .pl-sr .pl-cce,
.highlight .pl-sr .pl-sra,
.highlight .pl-sr .pl-sre
  { color: #0000ff; }
/* v=variable */
.highlight .pl-v
  { color: #202020; }
/* code */
.highlight .blob-code
  { color: #202020; }
/* line number */
.highlight .blob-num
  { color: #a0a0a0; }

}
