css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.cryptozoic.com") {


div#lower > div:first-child { display:none !important; }
div.foruminfo > img.forumicon { display:none !important; }
.threadbit .nonsticky a.threadstatus, .threadbit .sticky a.threadstatus { display:none !important; }

.postbitlegacy .userinfo { float:none !Important; width:auto !important; }
.postbitlegacy .userinfo > * { display:inline-block !important; }
.postbitlegacy .userinfo .userinfo_extra { display:none !important; }
.postbitlegacy .postbody { margin:0 !important; }

hr { display:none !Important; }
.userinfo .username_container span { font-size:22px !important; }
.postbitlegacy .userinfo a.username, .eventbit .userinfo a.username
{ max-width:auto !important; }
.thread_info, #footer { display:none !Important; }

.threadinfo.thread > div.inner:nth-child(2) { padding-left:9px !Important; }
.threadbit .threadstats { display:none !important; }
.threadbit .alt { background:none !important; }
.forumbit_post .foruminfo .forumdata .datacontainer { padding-left:9px !important; }

div[id*=post_message_], div[id*=post_message_] * { font-size:15px !important; color:#000 !important; }
.postbitlegacy .imlinks  { display:none !important; }

#newreplylink_bottom { display:none !important; }
.below_postlist .pagination_bottom .pagination { float:none !important; margin:0 auto !important; width:100% !important; height:50px !important; }
.below_postlist .pagination_bottom .pagination span:first-of-type { display:none !important; }
.below_postlist .pagination_bottom .pagination a { margin:0px !important; border:0 !important; top:0 !important; font-size:20px !important; text-align:center !important; width:auto !important; line-height:50px !important; padding:0 !important; height:50px !important; display:block !important; }
.below_postlist .pagination_bottom .pagination a[title*="Show results"], .below_postlist .pagination_bottom .pagination span.selected a { width:100px !important; }
.below_postlist .pagination_bottom .pagination .prev_next a { width:50px !Important; }
.below_postlist .pagination_bottom .pagination .first_last a { width:100px !Important; }



}
