/* i really want this to be global */

html:before{content: '' !important;
position: fixed !important;
outline: 2000px solid rgba(198, 185, 202, .18) !important; 
z-index: 99999 !important;
}

html:after{content: '' !important;
position: fixed !important;
width:100% !important;
height:100%!important;
top:0!important;left:0!important;
background: -moz-radial-gradient(center, ellipse cover,  rgba(41,19,11,0) 0%, rgba(41,19,11,0.27) 100%); 
background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,rgba(41,19,11,0)), color-stop(100%,rgba(41,19,11,0.27))); 
background: -webkit-radial-gradient(center, ellipse cover,  rgba(41,19,11,0) 0%,rgba(41,19,11,0.27) 100%); 
background: radial-gradient(ellipse at center,  rgba(41,19,11,0) 0%,rgba(41,19,11,0.27) 100%); 

z-index: 999999999 !important;
pointer-events:none!important;
}
