css:
@-moz-document url-prefix("http://rinkworks.com/rinkchat/") {
body {
	font-family:Helvetica Neue;
    color:#333;
    font-weight:300;
    font-size:14px;
    line-height:18px;
  	background-color:#f1f2f3;

}

body[onerror] {
	background-color:#fff;
}
b {
	font-weight:500;
}

frame[name="messages"] {
  padding:0 20px 0;
  background:#fff;

}
frame[name="list"] {
  border-left:1px solid #efefef;
}
frame[name="list"] body {
  background:#E6F9FF;
}
nobr, nobr + p {
  position:relative;
  left:-2px;
  z-index:100;
    padding:0 0 0 10px;


}
nobr b, nobr b font{
	font-size:13px !important;
  	text-transform:uppercase;
  	color:#0086B2;
}

font[size="-1"] {
	font-size:9px;
   	text-transform:uppercase;
	font-weight:400;
  letter-spacing:1px;
}
font[color="brown"] {
	color:#AAA;
}
font[color="red"] {
	color:#888;
}
font[color="green"] {
	color:#00BFFF;
}
font[color="purple"] {
	color:#0086B2;
}
font[color="#993311"] {
	color:#006080;
}
font[color="#0000b8"]{
  	color:#0086B2;
 }

a {	color:#00BFFF;}
a:hover {	color:#4DD2FF;}
a:visited {	color:#0086B2;}

nobr + p td{

  font-size:11px;
  height:20px;

}
frame body {
}



form table td:nth-child(1) {
	width:65%;
  font-size:12px;
} 
form table td:nth-child(3) {
	width:30%;
} 
form table td:nth-child(4) {
	width:5%;
} 
form table td input[type="text"] {
  width:70%;
  
}

#content center {display:none;}
}
