css:
@-moz-document domain("imo.im") {
.buddyitem .status,
.convbuttons #play,
#header ul
{
  display: none;
}


.buddygroup .bar
{
  background: hsl(240,50%,88%) !important;
  font-size: 1em !important;
  height: 1.5em !important;
  line-height: 1.5em !important;
}

.buddygroup .bar.collapsed
{
  background: hsl(240,40%,90%) !important;
}

.buddygroup .bar .sprite_icn_collapse,
.buddygroup .bar .sprite_icn_expand
{
  margin: .25em !important;
  padding: 0em !important;
}

.history-search-button
{
  left: .5em !important;
  top: 1em !important;
}

.history-search-container
{
  width: 25% !important;
  padding-right: 3em !important;
  padding-top: .5em !important;
}

.history-search-input
{
  margin: 0em !important;
  padding: 0em !important;
  padding-left: 2em !important;
  width: 100% !important;
}


#buddypanel
{
  width: 25% !important;
}

#main
{
  margin-right: 1px !important;
  right: 25% !important;
}
}
