css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http') {
    body [class*="cookie"], 
    body [class*="Cookie"],
    body [id*="cookie"], 
    body [id*="Cookie"]
    {
        display:none !important;
    }
    html
    {
        margin-top:0 !important;
    }
    
}

@-moz-document domain('interia.pl') {
    #inpl_cp_cnt
    {
        display:none !important;
    }
}

@-moz-document domain('demotywatory.pl') {
    div[style="position: fixed; left:0; right:0; bottom:0; height:120px; background-color:#eee; border-top: 1px solid black; z-index:1000; overflow: auto"]
    {
        display:none !important;
    }
}

@-moz-document domain('joemonster.org') {
    div[style="position: fixed; left:0; right:0; bottom:0; height:120px; background-color:#eee; border-top: 1px solid black; z-index:1000; overflow: auto"]
    {
        display:none !important;
    }
}

@-moz-document domain('goldenline.pl'){
    #topReminder{display:none !important;}
}

@-moz-document domain('nk.pl') {
    .cookie_privacy_info
    {
        padding-top:0 !important;
    }

    .cookie_privacy_info_bar
    {
        display:none !important;
    }
}


@-moz-document domain('wykop.pl') {
    .closableContainer
    {
        padding-top:0 !important;
    }

    .closableContainer
    {
        display:none !important;
    }
    .type-permanent + #nav {
        top: 0 !important;
    }
    .type-permanent + #nav + div {
        padding-top: 53px !important;
    }
}
