css:
@-moz-document domain("wired.jp") {
#social-share,
#sidebar-wrap,
[id^="banner-"],
#popin-slide-module-related,
.icon-back,
.icon-humberger
{ display: none !important; }

#article,
#article-asides {
  position: static !important;
  padding-top: 0 !important;
}

[id^="article-"] {
  display: flex;
  flex-direction: column;
  margin-top: 2rem;
}

#sidebar-wrap {
  height: auto !important;
}

.contents-main {
  width: 50.75rem;
  padding: 0;
}
.article-detail {
  float: none;
  width: auto;
  margin: 0;
}
.article-detail p {
  margin: 0 !important;
  padding: 0 4.75rem;
}
.article-detail p>img {
  margin: 2rem auto !important;
}

body {
  background-image: none;
}

#header {
  background-color: transparent;
  border: 0;
  position: static;
}

}
