css:
@-moz-document domain("japan.digitaldj-network.com") {

  #matomeSiteHeaderDummy, #matomeSiteHeader, #rightcell,
  #header, #sub, .blogparts2, .entry-tag, *[class*="ads"],
  .t-buttona, .bkm-bt, #headline, #zenback, .social, #extra,
  #adin, .entry-edit, .related-articles, .entry-footer, .t-button
  { display: none !important; }

  body {
    width: inherit !important;
    padding: 0 !important;
    background-color: #fee !important;
    background-image: none !important;
  }
  #wrapper {
    width: 675px !important;
    float: none !important;
    margin: 2em auto !important;
  }
  #content {
    width: 675px !important;
    padding: 0 20px !important;
    margin: 2em auto !important;
    border-radius: 4px;
    box-shadow: 0 1px 4px #ddd;
  }
  #content-in {
    margin: 0 auto !important;
  }
  .entry {
    width: 640px !important;
    padding: 10px 20px 70px 20px;
  }
  .entry-ttl {
    border: 0 !important;
  }
  .entry-ttl h2 {
    float: none !important;
    width: auto !important;
    margin: 0 auto;
  }

}
