@-moz-document domain(facebook.com) {
.fbIndex .loggedout_menubar_container {
  position: fixed !important;
  width: 420px !important;
  height: 82px !important;
  min-width: 0 !important;
  top: 50% !important;
  left: 50% !important;
  margin-top: -17px !important;
  margin-left: -210px !important;
  z-index: -1 !important;
}


.fbIndex .loggedout_menubar { width: auto !important }
.fbIndex .loggedout_menubar_container .rhloat,
.fbIndex .loggedout_menubar_container .rhloat { float: none !important }
.fbIndex .loggedout_menubar_container .rhloat img,
.fbIndex .loggedout_menubar_container .rhloat #login_form table { display: block !important; margin: 0 auto !important }

.fbIndex .loggedout_menubar_container .lfloat img { display: block; margin: -60px auto 20px !important; }



#SetAsHomepage_Callout {
  display: none;
}
}
